                    ("Sundays", context['sunday_attendances']),
                    ("Holidays", context['holiday_count']),
                    ("Leaves", context['leave_days']),
                    ("Full Days", context['full_days']),
                    ("Half Days", context['half_days']),
                    ("Late Punches", context['late_punches']),
